INSERT INTO `wp_posts` VALUES (106,1,'2010-05-13 16:59:07','2010-05-13 15:59:07','<div>Institution MVC Tests</div>\n<ul>\n	<li>Test rendering of all views</li>\n	<li>Read entities from the repository into the model\n<ul>\n	<li>RepositoryÂ returns null</li>\n</ul>\n</li>\n	<li>Creating entities\n<ul>\n	<li>Check that all model values are passed to the repository - this involves</li>\n</ul>\n</li>\n</ul>','','','inherit','open','open','','104-revision-2','','','2010-05-13 16:59:07','2010-05-13 15:59:07','',104,'http://www.jonathansewell.co.uk/index.php/2010/05/13/104-revision-2/',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (107,1,'2010-05-14 10:55:01','2010-05-14 09:55:01','<div>MVC Controller Tests</div>\n<ul>\n	<li>Test rendering of all views</li>\n	<li>Test reading entities from the repository into the model\n<ul>\n	<li>Test for when repositoryÂ returns null</li>\n</ul>\n</li>\n	<li>Creating entities\n<ul>\n	<li>Check that all model values are passed to the repository - this involves</li>\n	<li>Check PRG pattern used</li>\n</ul>\n</li>\n	<li>Editing entities\n<ul>\n	<li>Test for when repositoryÂ returns null</li>\n</ul>\n</li>\n</ul>\nCheck out \"all controller test\"','','','inherit','open','open','','104-revision-3','','','2010-05-14 10:55:01','2010-05-14 09:55:01','',104,'http://www.jonathansewell.co.uk/index.php/2010/05/14/104-revision-3/',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (108,1,'2010-05-14 12:27:17','2010-05-14 11:27:17','<div>MVC Controller Tests</div>\n<ul>\n	<li>Test rendering of all views</li>\n	<li>Test reading entities from the repository into the model\n<ul>\n	<li>Test for when repositoryÂ returns null</li>\n</ul>\n</li>\n	<li>Creating entities\n<ul>\n	<li>Check that all model values are passed to the repository method</li>\n	<li>Check PRG pattern used</li>\n</ul>\n</li>\n	<li>Editing entities\n<ul>\n	<li>Test model is populated from repository</li>\n</ul>\n</li>\n	<li>Updating entities\n<ul>\n	<li>Check that all model values are passed to the repository method</li>\n</ul>\n</li>\n	<li>Deleting entries\n<ul>\n	<li>Check that all model values are passed to the repository method</li>\n</ul>\n</li>\n</ul>\nCheck out \"all controller test\"','','','inherit','open','open','','104-revision-4','','','2010-05-14 12:27:17','2010-05-14 11:27:17','',104,'http://www.jonathansewell.co.uk/index.php/2010/05/14/104-revision-4/',0,'revision','',0);
